catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {


	if (isLoading || newValue == '') {
		return;
	}

	var startDate    = g_form.getValue('start_date');
	var endDate      = g_form.getValue('end_date_if_temporary_change');

	if (endDate) {
		var startDateArr = startDate.split('/');
		var endDateArr   = endDate.split('/');

		var startDateTime = startDateArr[2] + '-' + startDateArr[1] + '-' + startDateArr[0] + ' 12:00:00';
		var endDateTime   = endDateArr[2] + '-' + endDateArr[1] + '-' + endDateArr[0] + ' 12:00:00';
		//alert("PHW: " + endDateTime);

		var compDateGa = new GlideAjax('global.scAjaxUtil');
		compDateGa.addParam('sysparm_name', 'compareDateTime');
		compDateGa.addParam('startDateTime', startDateTime);
		compDateGa.addParam('endDateTime', endDateTime);
		compDateGa.getXML(response_parse);
	} // End of endDate

	function response_parse(response) {
		var answer      = response.responseXML.documentElement.getAttribute("answer");
		//alert("PHW: " + answer);

		/*
		 * Compare StartDateTime to EndDateTime and return result
		 * startDateTime and endDateTime format "2023-02-13 12:00"
		 * If result = 0; StartDate = EndDate
		 * if result = 1; StartDate > EndDate
		 * if result =-1; StartDate < End date
		 */

		if (answer != '-1') {
			g_form.addErrorMessage('Start date must be before End date');
			g_form.clearValue('start_date');
		} 
	}


}
