catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	if (isLoading || newValue == '') {
        return;
    }

    // Populate Requestor Name
	var userId        = g_user.userID;
	var userName      = g_user.userName;
	var userFirstName = g_user.firstName;
	var userLastName  = g_user.lastName;
	var userEmail     = g_user.email;
	var reqForLoc     = g_form.getValue('requested_for_location');

	if ( reqForLoc == 'Support Office') {
		// Clear Store Variables
		g_form.clearValue('requestor_name');
		g_form.clearValue('requested_for_team_member_name');
		g_form.clearValue('requested_for_work_location');
		g_form.clearValue('requested_for_email_address');


		g_form.setValue('requestor_name_so', userId);


	} else { // Store
		// Clear Support Office Variables
		g_form.clearValue('requestor_name_so');
		g_form.clearValue('requested_for_team_member_name_so');
		g_form.clearValue('requested_for_work_location_so');
		g_form.clearValue('requested_for_team_member_email_address_so');

		g_form.setValue('requestor_name', userFirstName + " " + userLastName);

		var userInfoGa = new GlideAjax('global.scAjaxUtil');
		userInfoGa.addParam('sysparm_name', 'getUserInfoPayload');
		userInfoGa.addParam('user_sys_id', userId);
		userInfoGa.getXML(response_parse);

	}

	function response_parse(response) {
		var answer      = response.responseXML.documentElement.getAttribute("answer");
		//alert("PHW: " + answer);
		var userPyldObj = JSON.parse(answer);
		// Variables ending with so - Support Office
		g_form.setValue('requested_for_work_location', userPyldObj['location']);
	}

}
