catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	if (isLoading || newValue == '') {
       return;
    }

    //Populate "Requested for" Information for Support Office (SO)
	var userId        = g_form.getValue('requested_for_team_member_name_so');
	var reqForLoc     = g_form.getValue('requested_for_location');

	if (reqForLoc == "Support Office") {
		var userInfoGa = new GlideAjax('global.scAjaxUtil');
		userInfoGa.addParam('sysparm_name', 'getUserInfoPayload');
		userInfoGa.addParam('user_sys_id', userId);
		userInfoGa.getXML(response_parse);
	} // *** End of if positionType ***
	function response_parse(response) {
		var answer      = response.responseXML.documentElement.getAttribute("answer");
		//alert("PHW: " + answer);
		var userPyldObj = JSON.parse(answer);

		// Variables ending with so - Support Office
		g_form.setValue('requested_for_team_member_email_address_so', userPyldObj['email']);
		g_form.setValue('requested_for_work_location_so', userPyldObj['location']);


		g_form.setReadOnly('requested_for_team_member_email_address_so', true);

	}

}
