sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var ritmSysId           = current.getValue('sys_id');
	var contactEmailAddress = "";
	var requestorName       = "";
	var requestorEmailAddr  = "";

	// Get "Contact Email Address" and "Requestor" email address
	var ritmGr = new GlideRecord('sc_req_item');
	ritmGr.get('sys_id', ritmSysId);
	var var_list = ritmGr.variables;

	for (var varName in var_list) {
		if ( (varName == "contact_email_address") && (var_list[varName]) ) { 
			contactEmailAddress = var_list[varName]; 
		}
		if ( (varName == "contact_email_address_so") && (var_list[varName]) ) { 
			contactEmailAddress = var_list[varName]; 
		}
		if ( varName == "requestor_name" ) { requestorName = var_list[varName]; }  
	}

	// Get Requestor email address for sys_user table
	var userGr = new GlideRecord('sys_user');
	userGr.addQuery('name', requestorName);
	userGr.query();
	if (userGr.next()) {
		requestorEmailAddr = userGr.getValue('email');
	}


	// Send Email via inserting event
	gs.eventQueue('x_aupi_workjam.email_notif_ritm_opened',current,contactEmailAddress,requestorEmailAddr);

})(current, previous);
